import React, { Component } from "react";
import { Button, Image } from 'react-bootstrap';
import './RecipePage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClock } from '@fortawesome/free-solid-svg-icons';


export default class RecipePage extends Component {
  render() {

    const ingredients = [
      {
        amount: "1 lb",
        item: "ground beef",
      }, 
      {
        amount: "1 whole",
        item: "zucchini",
      }, 
      {
        amount: "2 cups",
        item: "spinach",
      }, 
      {
        amount: "0.5 lb",
        item: "noodles",
      }, 
      {
        amount: "24 oz",
        item: "marinara",
      }, 
    ]

    return (

      <div className="RecipePage">
        <div className="lander">
          <span>
            <h1 id="title">Spaghetti</h1>
            <Button variant="btn btn-success" id="shopping">Add to Shopping Cart</Button>
            <div id="timer">
              <em id="timeamt">30 minutes</em>
              <FontAwesomeIcon icon={faClock} id="timeicon"/>
            </div>
          </span>
          <span>
            <div id="ingredients">
              <h3 id="ingr_title">Ingredients</h3>
              <UnorderedRecipes />
            </div>
            <div id="right">
              <img src="https://c.ndtvimg.com/2020-01/n7thfo2o_spaghetti_625x300_28_January_20.jpg" id="image"></img>
            </div>
          </span>
          <div id="instructions">
            <h3 id="instr_title">Instructions</h3>
              <ol id="ingr_list">
                <li>Demo step 1</li>
                <li>Demo step 2</li>
                <li>Demo step 3</li>
                <li>Demo step 4</li>
                <li>Demo step 5</li>
              </ol>
          </div>
        </div>
      </div>
    );
  }
}
